package main

import (
	"fmt"
)

func minFlips(grid [][]int) int {
	n := len(grid)
	m := len(grid[0])

	ans := 0

	// Loop through the 2x2 sub-grids
	for i := 0; i < n/2; i++ {
		for j := 0; j < m/2; j++ {
			elements := []int{
				grid[i][j],
				grid[n-i-1][j],
				grid[i][m-j-1],
				grid[n-i-1][m-j-1],
			}

			ones := 0
			for _, v := range elements {
				ones += v
			}

			if ones > 2 {
				ans += 4 - ones
			} else {
				ans += ones
			}
		}
	}

	possible := 0
	count := 0

	// Handle the middle row if n is odd
	if n%2 != 0 {
		for j := 0; j < m/2; j++ {
			if grid[n/2][j] != grid[n/2][m-j-1] {
				ans++
				possible++
			} else {
				if grid[n/2][j] == 1 {
					count++
				}
			}
		}
	}

	// Handle the middle column if m is odd
	if m%2 != 0 {
		for i := 0; i < n/2; i++ {
			if grid[i][m/2] != grid[n-i-1][m/2] {
				ans++
				possible++
			} else {
				if grid[i][m/2] == 1 {
					count++
				}
			}
		}
	}

	// Handle the central element if both n and m are odd
	if n%2 != 0 && m%2 != 0 && grid[n/2][m/2] == 1 {
		ans++
	}

	if possible == 0 && count%2 != 0 {
		ans += 2
	}

	return ans
}
