class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int count = 0;
        for(int i=0 ; i < n/2 ; i++){
            for(int j=0 ; j < m/2 ; j++){
                int c[2] = {0, 0};
                c[g[i][j]] += 1;
                c[g[n-i-1][j]] += 1;
                c[g[i][m-j-1]] += 1;
                c[g[n-i-1][m-j-1]] += 1;
                count += min(c[0], c[1]);
            }
        }
        int c1 = 0, c2 = 0;
        bool ok1 = false, ok2 = false;
        if(n&1){
            for(int j=0 ; j < m/2 ; j++){
                if(g[n/2][j] != g[n/2][m-j-1]){
                    count += 1;
                    ok1 = true;
                }else c1 += 2 * g[n/2][j];
            }
        }
        if(m&1){
            for(int i=0 ; i<n/2 ; i++){
                if(g[i][m/2] != g[n-i-1][m/2]){
                    ok2 = true;
                    count += 1;
                }
                else c2 += 2 * g[i][m/2];
            }
        }
        if((n&1)^(m&1)){
            if(c1%4 and (!ok1)) count += 2;
            if(c2%4 and (!ok2)) count += 2;
        }
        if(n&1 and m&1) {
            if((c1 + c2)%4 and (ok1 == false and ok2 == false))
                count += 2;
            count += g[n/2][m/2];
        }
        return count;
    }
};
