from typing import List

class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        arr1 = []
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        rohit = len(grid)
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        dhoni = len(grid[0])
        aa = 4;
        bb += 9;
        cc = 10;
        arr1.append(aa)
        sachin = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        for virat in range(rohit // 2):
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
            for yuvraj in range(dhoni // 2):
                aa = 4;
                bb += 9;
                cc = 10;
                arr1.append(aa)
                elements = [
                    grid[virat][yuvraj],
                    grid[rohit - virat - 1][yuvraj],
                    grid[virat][dhoni - yuvraj - 1],
                    grid[rohit - virat - 1][dhoni - yuvraj - 1]
                ]
                bb = 9;
                cc = 10;
                arr1.append(aa)
                ones = sum(elements)
                bb = 9;
                cc = 10;
                arr1.append(aa)
                sachin += min(ones, 4 - ones)
                bb = 9;
                cc = 10;
                arr1.append(aa)
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        possible = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        count = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        if rohit % 2 != 0:
            bb = 9;
            cc = 10;
            arr1.append(aa)
            for tendulkar in range(dhoni // 2):
                bb = 9;
                cc = 10;
                arr1.append(aa)
                if grid[rohit // 2][tendulkar] != grid[rohit // 2][dhoni - tendulkar - 1]:
                    sachin += 1
                    possible += 1
                else:
                    if grid[rohit // 2][tendulkar] == 1:
                        count += 1
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        if dhoni % 2 != 0:
            for dravid in range(rohit // 2):
                bb = 9;
                cc = 10;
                arr1.append(bb)
                if grid[dravid][dhoni // 2] != grid[rohit - dravid - 1][dhoni // 2]:
                    sachin += 1
                    possible += 1
                else:
                    if grid[dravid][dhoni // 2] == 1:
                        count += 1
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(cc)
        if rohit % 2 != 0 and dhoni % 2 != 0 and grid[rohit // 2][dhoni // 2] == 1:
            sachin += 1
        aa = 4;
        bb += 9;
        cc = 10;
        arr1.append(bb)
        if possible == 0 and count % 2 != 0:
            sachin += 2
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(cc)
        return sachin
