class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m= grid.size(),n=grid[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int x= grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1];
                //cout<<x<<endl;
                if(x==1) ans+=1;
                else if(x==2) ans+=2;
                else if(x==3) ans+=1;
                else ans+=0;
            }
        }
        int res=0;
        bool flag=false;
        if(m&1){
            for(int j=0;j<n/2;j++){
                int y=m/2;
                int x=grid[y][j]+grid[y][n-j-1];
                if(x==1) {ans++;flag=true;}
                else res+=x;
            }
        }
        if(n&1){
            for(int i=0;i<m/2;i++){
                int y= n/2;
                int x=grid[i][y]+grid[m-i-1][y];
                if(x==1){ans++;flag=true;}
                else res+=x;
            }
        }
        if(m&1 && n&1 && grid[m/2][n/2]==1) ans++;
        res%=4;
        if(res==2 && flag==false) ans+=res;
        return ans;
    }
};
