class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int m = grid.size();
        int n = grid[0].size();
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                vector<int> cnt(2);
                cnt[grid[i][j]]++;
                cnt[grid[i][n - 1 - j]]++;
                cnt[grid[m - 1 - i][j]]++;
                cnt[grid[m - 1 - i][n - 1 - j]]++;
                res += min(cnt[0], cnt[1]);
            }
        }

        int tot = 0;
        int cnt1 = 0;
        if (m & 1) {
            int i = m / 2;
            for (int j = 0; j < n / 2; j++) {
                if (grid[i][j] != grid[i][n - 1 - j]) {
                    tot++;
                } else {
                    if (grid[i][j] == 1) {
                        cnt1 += 2;
                    }
                }
            }
        }
        if (n & 1) {
            int j = n / 2;
            for (int i = 0; i < m / 2; i++) {
                if (grid[i][j] != grid[m - 1 - i][j]) {
                    tot++;
                } else {
                    if (grid[i][j] == 1) {
                        cnt1 += 2;
                    }
                }
            }
        }

        if ((m & 1) == 1 && (n & 1) == 1) {
            if (tot > 0) {
                return res + tot + grid[m / 2][n / 2]; 
            } else {
                if ((cnt1 % 4) != 0) {
                    return res + 2 + grid[m / 2][n / 2];
                } else {
                    return res + grid[m / 2][n / 2];
                }
            }
        } else {
            if (tot > 0) {
                return res + tot;
            } else {
                if ((cnt1 % 4) != 0) {
                    return res + 2;
                } else {
                    return res;
                }
            }
        }
    }
};
