class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = n * m;
        int sum1 = 0;
        int sum2 = 0;
        int cnt1 = 0, cnt2 = 0, cnt3 = 0;
        for(int i = 0; i < n / 2; i++) {
           for(int j = 0; j < m / 2; j++) {
               int x = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
               sum1 += Math.min(x, 4 - x);
           }
        }

        if(n % 2 == 1) {
            for(int j = 0; j < m / 2; j++) {
                 int x = grid[n / 2][j] +  grid[n / 2][m - j - 1];
                 sum2 += x % 2;
                 if(x == 2) {
                     cnt1 += x;
                 }
            }
        }
         if(m % 2 == 1) {
            for(int i = 0; i < n / 2; i++) {
                 int x = grid[i][m / 2] +  grid[n - i - 1][m / 2];
                 sum2 += x % 2;
                if(x == 2) {
                    cnt1 += x;
                }
            }
        }
        if(sum2 == 0 && cnt1 % 4 != 0)sum1 += 2;
        if(n % 2 > 0 && m % 2 > 0 && grid[n  / 2][m / 2] == 1) {
            sum1++;
        }

        ans = Math.min(ans, sum1 + sum2);
        return ans;
    }
}
