class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])
        ans = 0
        for i in range(n // 2):
            for j in range(m // 2):
                o = z = 0
                if grid[i][j] == 1:o += 1
                else:z += 1
                if grid[i][m - j - 1] == 1:o += 1
                else:z += 1
                if grid[n - i - 1][j] == 1:o += 1
                else:z += 1
                if grid[n - i - 1][m - j - 1] == 1:o += 1
                else:z += 1
                ans += min(o,z)
        if n % 2 != 0 and m % 2 != 0 and grid[n // 2][m // 2] == 1:ans += 1
        o = oz = 0
        if n % 2 != 0:
            for i in range(m // 2):
                if grid[n // 2][i] != grid[n // 2][m - i - 1]:
                    oz += 1
                elif grid[n // 2][i] == 1:
                    o += 1
        if m % 2 != 0:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - i - 1][m // 2]:
                    oz += 1
                elif grid[i][m // 2] == 1:
                    o += 1
        if o % 2 != 0:
            if oz > 0:
                oz -= 1
                ans += 1
            else:
                ans += 2
        return ans + oz


