class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n = len(grid),len(grid[0])
        op = 0
        for i in range(m//2):
            for j in range(n//2):
                ones=grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1]
                op+=min(ones,4-ones)
        # 如果有奇数行
        doubleOnes = 0
        adds = 0
        if m%2==1:
            r = m//2
            left,right = 0,n-1
            while left<right:
                if grid[r][left]!=grid[r][right]:
                    adds+=1
                elif grid[r][left]==1 and grid[r][right]==1:
                    doubleOnes+=1
                left+=1
                right-=1
        if n%2==1:
            c = n//2
            up,down = 0,m-1
            while up<down:
                if grid[up][c]!=grid[down][c]:
                    adds+=1
                elif grid[up][c]==1 and grid[down][c]==1:
                    doubleOnes+=1
                up+=1
                down-=1
        if doubleOnes%2==1 and adds==0:
            adds+=2
        if m%2==1 and n%2==1 and grid[m//2][n//2]==1:
            adds+=1
        return adds+op
