using namespace std;

class Solution {
public:
    int max(int a,int b){
        if(a<=b)return b;
        return a;
    }
    int min(int a,int b){
        if(a>=b)return b;
        return a;
    }
    int sqt(int a){
        return a;
    }
    int minFlips(vector<vector<int>>& grid) {
        int output = 0, count = 0, yo = 0;
        int rows = grid.size();
        int cols = grid[0].size();

        for (int i = 0; i < (rows + 1) / 2; ++i) {
            for (int j = 0; j < (cols + 1) / 2; ++j) {
                int op = grid[i][j] + grid[i][cols - j - 1] + grid[rows - i - 1][j] + grid[rows - i - 1][cols - j - 1];

                if ((i == rows - i - 1) && (j == cols - j - 1)) {
                    output += op / 4;
                } else if (i == rows - i - 1) {
                    op /= 2;
                    output += min(op, 2 - op);
                    if (op == 2) count++;
                    if (op == 1) yo++;
                } else if (j == cols - j - 1) {
                    op /= 2;
                    output += min(op, 2 - op);
                    if (op == 2) count++;
                    if (op == 1) yo++;
                } else {
                    output += min(op, 4 - op);
                }
            }
        }

        if (count % 2 != 0 && yo == 0) {
            output += 2;
        }

        return output;
    }
};
