class Solution {
    fun minFlips(grid: Array<IntArray>): Int {
        val map = IntArray(5)
        for (i in 0 until grid.size / 2) {
            for (j in 0 until grid[0].size / 2) {
                val a = grid[i][j]
                val b = grid[grid.size - i - 1][j]
                val c = grid[i][grid[0].size - j - 1]
                val d = grid[grid.size - i - 1][grid[0].size - j - 1]
                val s = a + b + c + d
                map[s]++
            }
        }
        // print("map: ${map.joinToString()}\n")
        var min = map[1] + map[3] + 2 * map[2]

        // print("min: $min\n")
        //以上都是4的倍数 不要动了
        var count1 = 0
        var count = 0


        if (grid.size % 2 == 1) {
            for (j in 0 until grid[0].size / 2) {
                val i = grid.size / 2
                val a = grid[i][j]
                val b = grid[i][grid[0].size - j - 1]
                if (a != b) count++
                if (a == 1) count1++
                if (b == 1) count1++

            }
        }

        if (grid[0].size % 2 == 1) {
            for (i in 0 until grid.size / 2) {
                val j = grid[0].size / 2
                val a = grid[i][j]
                val b = grid[grid.size - i - 1][j]
                if (a != b) count++
                if (a == 1) count1++
                if (b == 1) count1++
            }
        }

        val center = if (grid[0].size % 2 == 1 && grid.size % 2 == 1 && grid[grid.size / 2][grid[0].size / 2] == 1) 1 else 0
        // 中间的1一定要变成0
        return min + center + Math.max(count, Math.min(4 - count1 % 4, count1 % 4))
    }
}
