class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        m1, n1 = len(grid)//2, len(grid[0])//2
        m2, n2 = (len(grid)+1)//2, (len(grid[0])+1)//2
        ans = 0
        for i in range(m1):         
            for j in range(n1): 
                c = 0
                if grid[i][j]:
                    c += 1
                if grid[m-1-i][j]:
                    c += 1
                if grid[i][n-1-j]:
                    c += 1
                if grid[m-1-i][n-1-j]:
                    c += 1

                if c == 0 or c == 4:
                    ans += 0
                elif c == 1 or c == 3:
                    ans += 1
                elif i == m1 or j == n1:
                    ans += 0
                else:
                    ans += 2
#                print("a", i, j, c, ans)

        if m % 2:
            c = 0
            c1 = 0
            for j in range(n1):
                if grid[m1][j] != grid[m1][n-1-j]:
                    c += 1
                elif grid[m1][j]:
                    c1 += 2
#            print("b", m1, n1, c, c1, ans)

            if n % 2:
                if grid[m1][n1]:
                    ans += 1
                for i in range(m1):
                    if grid[i][n1] != grid[m-1-i][n1]:
                        c += 1
                    elif grid[i][n1]:
                        c1 += 2
            ans += c
#            print(ans, c, c1)
            if c == 0 and c1 % 4:
                ans += 2
        else:
            if n % 2:
                c = 0
                c1 = 0
                for i in range(m1):
                    if grid[i][n1] != grid[m-1-i][n1]:
                        c += 1
                    elif grid[i][n1]:
                        c1 += 2
                ans += c
                if c == 0 and c1 % 4:
                    ans += 2
        return ans
