class Solution {
    int m, n;
public:
    int minFlips(vector<vector<int>>& grid) {
        m=grid.size(), n=grid[0].size();
        int ret=0;
        for (int i=0; i<m/2; i++){
            for (int j=0; j<n/2; j++){
                int c=0;
                c+=grid[i][j];
                c+=grid[m-1-i][j];
                c+=grid[m-1-i][n-1-j];
                c+=grid[i][n-1-j];
                ret+=min(4-c, c);
            }
        }
        // cout << "four " << ret << endl;
        int smv=0, dif=0;
        if (n%2==1){
            for (int i=0; i<m/2; i++){
                smv+=grid[i][n/2];
                smv+=grid[m-1-i][n/2];
                if (grid[i][n/2]!=grid[m-1-i][n/2])
                    dif++;
            }
            // cout << smv << " " << dif << endl;
        }
        // cout << "n " << ret << endl;

        if (m%2==1){
            for (int j=0; j<n/2; j++){
                smv+=grid[m/2][j];
                smv+=grid[m/2][n-1-j];
                if (grid[m/2][j]!=grid[m/2][n-1-j])
                    dif++;
            }
        }
        ret+=dif;
        smv%=4;
        smv=min(smv, 4-smv);
        if (smv>dif)
            ret+=smv%4;
        // cout << "m " << ret << endl;

        if (m%2==1 && n%2==1)
            if (grid[m/2][n/2]==1)
                ret++;
        return ret;

    }
};
