#include <vector>
#include <algorithm>

using namespace std;

#define vi vector<int>
#define vvi vector<vi>

class Solution {
public:
    int sub(vvi& g, int n, int m) {
        int f = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                vi e = {
                    g[i][j],
                    g[n - i - 1][j],
                    g[i][m - j - 1],
                    g[n - i - 1][m - j - 1]
                };

                int o = count(e.begin(), e.end(), 1);
                f += min(o, 4 - o);
            }
        }
        return f;
    }

    int row(vvi& g, int n, int m, int& p, int& c) {
        int f = 0;
        if (n % 2 != 0) {
            int r = n / 2;
            for (int j = 0; j < m / 2; ++j) {
                if (g[r][j] != g[r][m - j - 1]) {
                    f += 1;
                    p += 1;
                } else {
                    if (g[r][j] == 1) {
                        c += 1;
                    }
                }
            }
        }
        return f;
    }

    int col(vvi& g, int n, int m, int& p, int& c) {
        int f = 0;
        if (m % 2 != 0) {
            int d = m / 2;
            for (int i = 0; i < n / 2; ++i) {
                if (g[i][d] != g[n - i - 1][d]) {
                    f += 1;
                    p += 1;
                } else {
                    if (g[i][d] == 1) {
                        c += 1;
                    }
                }
            }
        }
        return f;
    }

    int center(vvi& g, int n, int m) {
        if (n % 2 != 0 && m % 2 != 0 && g[n / 2][m / 2] == 1) {
            return 1;
        }
        return 0;
    }

    int minFlips(vvi& g) {
        int n = g.size();
        int m = g[0].size();

        int p = 0;
        int c = 0;
        int a = 0;

        a += sub(g, n, m);
        a += row(g, n, m, p, c);
        a += col(g, n, m, p, c);
        a += center(g, n, m);

        if (p == 0 && c % 2 != 0) {
            a += 2;
        }

        return a;
    }
};
