class Solution {
public:
int minFlips(vector<vector<int>>& g) {
vi a,b;
int n=(int)g.size();
int m=(int)g[0].size();
if(n%2==1){
    for(int i=0;i<m;i++){
        if(m%2==1 && i==m/2) continue;
        a.push_back(g[n/2][i]);
    }
}
if(m%2==1){
    for(int i=0;i<n;i++){
        if(n%2==1 && i==n/2) continue;
        b.push_back(g[i][m/2]);
    }
}
int op=0;
for(int i=0;i<=n/2-1;i++){
    for(int j=0;j<=m/2-1;j++){
        int n1=0;
        if(g[i][j]==1) n1++;
        if(g[n-1-i][j]==1) n1++;
        if(g[i][m-1-j]==1) n1++;
        if(g[n-1-i][m-1-j]==1) n1++;
        op+=min(n1,4-n1);
    }
}
if(n % 2 == 1 && m % 2 == 1 && g[n/2][m/2]==1) op++;
int l1=(int)a.size();
int l2=(int)b.size();
int smr=0;
int dfr=0;
for(int i=0;i<=l1/2-1;i++){
    if(a[i]==a[l1-i-1]) {
        if(a[i]==1) smr++;
    }
    else dfr++;
}
int smc=0;
int dfc=0;
for(int i=0;i<=l2/2-1;i++){
    if(b[i]==b[l2-i-1]) {
        if(b[i]==1) smc++;
    }
    else dfc++;
}
int ans=INT32_MAX;
if(smr % 2 == 0 || dfr > 0){
    if(smc % 2 == 0 || dfc > 0) return op+dfc+dfr;
    else ans=min(ans,op+dfr+dfc+2);
}
else{
    if(smc % 2 == 0 || dfc > 0) ans=min(ans,op+dfr+dfc+2);
    else ans=min(ans,op+dfr+dfc+4);
}
if(smr % 2 == 1 || dfr > 0){
    if(smc % 2 == 1 || dfc > 0) return op+dfc+dfr;
    else ans=min(ans,op+dfr+dfc+2);
}
else{
    if(smc % 2 == 1 || dfc > 0) ans=min(ans,op+dfr+dfc+2);
    else ans=min(ans,op+dfr+dfc+4);
}
return ans;
}
};
