class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        if(grid.size()==1&&grid[0].size()==1){
            if(grid[0][0])return 1;
            return 0;
        }
        int total=0;
        if(grid.size()%2 && grid[0].size()%2){
            total+=grid[grid.size()/2][grid[0].size()/2];
        }
        for(int i=0;i<grid.size()/2;i++){
            for(int j=0;j<grid[0].size()/2;j++){
                int ones=0;
                int zeros=0;
                if(grid[i][j]){ones++;}else{zeros++;}
                if(grid[i][grid[0].size()-j-1]){ones++;}else{zeros++;}
                if(grid[grid.size()-i-1][j]){ones++;}else{zeros++;}
                if(grid[grid.size()-i-1][grid[0].size()-j-1]){ones++;}else{zeros++;}
                //cout<<ones<<' '<<zeros<<'\n';
                if(ones==4||zeros==4)continue;
                if(ones==1||ones==3)total++;
                else total+=2;
            }
        }
        int misfits=0;
        int loaded=0;
        if(grid.size()%2){
            for(int i=0;i<grid[0].size()/2;i++){
                if(grid[grid.size()/2][i]!=grid[grid.size()/2][grid[0].size()-i-1]){
                    misfits++;
                }else{
                    if(grid[grid.size()/2][i]==1)loaded+=1;
                }
            }
        }
        if(grid[0].size()%2){
            for(int i=0;i<grid.size()/2;i++){
                if(grid[i][grid[0].size()/2]!=grid[grid.size()-i-1][grid[0].size()/2]){
                    misfits++;
                }else{
                    if(grid[i][grid[0].size()/2]==1)loaded+=1;
                }
            }
        }
        total+=misfits;
        if(loaded%2){
            if(misfits==0)total+=2;
        }
        //cout<<misfits<<' '<<loaded;
        return total;
    }
};
