class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        int i=0;
        int j=n-1;
        int ans=0;
        int cnt1=0;
        bool f=0;
        while(i<j) {
            int l=0;
            int r=m-1;
            while(l<r) {
                int cnt=0;
                if(a[i][l]==0)
                {
                    cnt++;
                }    
                if(a[i][r]==0) {
                    cnt++;
                }
                if(a[j][l]==0) {
                    cnt++;
                }
                if(a[j][r]==0) {
                    cnt++;
                }
                ans+=min(cnt,4-cnt);
                l++;
                r--;
            }
            if(l==r) {
                if(a[i][l]!=a[j][r]) {
                    ans++;
                    f=1;
                }
                else if(a[i][l]==1) {
                    cnt1+=2;
                }
            }
            i++;
            j--;
        }
        if(i==j) {
            int l=0;
            int r=m-1;
            while(l<r) {
                if(a[i][l]!=a[i][r]) {
                    ans++;
                    f=1;
                }
                else if(a[i][l]==1) {
                    cnt1+=2;
                }
                l++;
                r--;
            }
            if(l==r && a[i][l]==1) {
                ans++;
            }
        }
        // cout<<ans<<" ";
        if(cnt1%4==2 && f==0) {
            ans+=2;
        }
        return ans;
    }
};
