class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int m = v.size() , n = v[0].size();
        int ans = 0;
        for(int i = 0 ; i < m / 2 ; i++) {
            for(int j = 0 ; j < n / 2 ; j++) {
                int tep = 0;
                if(v[i][j] == 1) tep++;
                if(v[i][n - j - 1] == 1) tep++;
                if(v[m - 1 - i][j] == 1) tep++;
                if(v[m - 1 - i][n - 1 - j] == 1) tep++;
                ans += min(tep , 4 - tep);
            }
        }
        int y = 0 , t = 0;
        if(m % 2) {
            int k = m / 2;
            int l = 0 , r = n - 1;
            while(l < r) {
                if(v[k][l] != v[k][r]) t++;
                else if(v[k][l] == 1) y += 2;
                l++;
                r--;
            }
        }
        if(n % 2) {
            int k = n / 2;
            int l = 0 , r = m - 1;
            while(l < r) {
                if(v[l][k] != v[r][k]) t++;
                else if(v[l][k] == 1) y += 2;
                l++;
                r--;
            }
        }
        if(m % 2 && n % 2) {
            if(v[m / 2][n / 2] == 1) ans++;
        }
        y %= 4;
        ans += t;
        if(y != 0 && t == 0) ans += y;
        return ans;
    }
};
