from typing import List

class Solution:
    def minFlips(self, mat: List[List[int]]) -> int:
        r, c = len(mat), len(mat[0])
        flp, extra, ones = 0, 0, 0

        for i in range(r // 2):
            for j in range(c // 2):
                cells = [
                    mat[i][j],
                    mat[r - i - 1][j],
                    mat[i][c - j - 1],
                    mat[r - i - 1][c - j - 1]
                ]
                cnt_1 = sum(cells)
                flp += min(cnt_1, 4 - cnt_1)
        if c % 2:
            mid_c = c // 2
            for i in range(r // 2):
                if mat[i][mid_c] != mat[r - i - 1][mid_c]:
                    flp += 1
                    extra += 1
                else:
                    if mat[i][mid_c] == 1:
                        ones += 1
        if r % 2:
            mid_r = r // 2
            for j in range(c // 2):
                if mat[mid_r][j] != mat[mid_r][c - j - 1]:
                    flp += 1
                    extra += 1
                else:
                    if mat[mid_r][j] == 1:
                        ones += 1



        if r % 2 and c % 2 and mat[mid_r][mid_c] == 1:
            flp += 1

        if extra == 0 and ones % 2:
            flp += 2

        return flp
