class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans =0;
        int count2 = 0, count1 = 0, fixable = 0;
        for(int i=0;i<n;i++){
        // grid[0][0] = grid[n-1][0] = grid[n-1][m-1] = grid[0][m-1]
        // grid[0][1] = grid[n-1][1] = grid[n-1][m-2] = grid[0][m-2]
        // grid[1][0] = grid[n-2][0] = grid[n-2][m-1] = grid[1][m-1]
            for(int j=0;j<m;j++){
                int mi = n-i-1;
                int mj = m-j-1;
                set<pair<int,int>> s;
                int c = grid[i][j] + grid[i][mj] + grid[mi][j] + grid[mi][mj];

                s.insert({i, j});
                s.insert({i, mj});
                s.insert({mi, j});
                s.insert({mi, mj});

                if(s.size() == 2){
                    // for(auto p: s){
                    //     cout << "->>"<< p.first << " "<<p.second << "\n";
                    // }
                    if(c == 4) {count2++;}
                    else if(c == 2) fixable++;
                } else if(s.size() == 1){
                    if(c == 4) {count1++;}
                }
                ans += min(c, 4-c);
            }
        }
        if(count1){
            ans += 4;
        }
        count2/=2;
        if(count2 % 2){
            if(fixable) {}
            else ans += 2 * 4;
        }
        return ans/4;
    }
};
