class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])
        ans1=0
        ans2=0
        ans=0
        for i in range(n//2):
            for j in range(m//2):
                a=grid[i][j]
                b=grid[i][m-1-j]
                c=grid[n-1-i][j]
                d=grid[n-1-i][m-1-j]
                if a+b+c+d<2:
                    grid[i][j]=0
                    grid[i][m-1-j]=0
                    grid[n-1-i][j]=0
                    grid[n-1-i][m-1-j]=0
                    ans+=a+b+c+d
                else:
                    grid[i][j]=1
                    grid[i][m-1-j]=1
                    grid[n-1-i][j]=1
                    grid[n-1-i][m-1-j]=1
                    ans+=4-a-b-c-d
        s=0
        if n%2!=0:
            i=n//2
            for j in range(m//2):
                if grid[i][j]!=grid[i][m-1-j]:
                    s+=1
                    grid[i][j]=grid[i][m-1-j]
        if m%2!=0:
            j=m//2
            for i in range(n//2):
                if grid[i][j]!=grid[n-1-i][j]:
                    s+=1
                    grid[i][j]=grid[n-1-i][j]
        mid=False
        if n%2!=0 and m%2!=0 and grid[n//2][m//2]==1:
            s+=1
            grid[n//2][m//2]=0
            mid=True
        x=0
        for i in range(n):
            for j in range(m):
                x+=grid[i][j]
        if x%4!=0:
            if mid:
                if s<=1:
                    s+=2
            else:
                if s<=0:
                    s+=2
        return ans+s
