void help(pair<int,int>& c, int n)
{
    if(n==1)
        ++c.first;
    else
        ++c.second;
}
class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        for(int iu=0,id=grid.size()-1;iu<id;iu++,id--)
        {
            for(int jl=0,jr=grid[0].size()-1;jl<jr;jl++,jr--)
            {
                pair<int,int> c;
                help(c,grid[iu][jl]);
                help(c,grid[iu][jr]);
                help(c,grid[id][jl]);
                help(c,grid[id][jr]);
                ans+=min(c.first,c.second);
            }
        }
        int c1=0;
        int o1=0;
        if(grid.size()%2==1)
        {
            int i=grid.size()/2;
            for(int j=0,k=grid[0].size()-1;j<k;j++,k--)
            {
                if(grid[i][j]!=grid[i][k])
                {
                    ++c1;
                }
                else if(grid[i][j]==1)
                {
                    ++o1;
                }
            }
        }
        int c2=0;
        int o2=0;
        if(grid[0].size()%2==1)
        {
            int j=grid[0].size()/2;
            for(int i=0,k=grid.size()-1;i<k;i++,k--)
            {
                if(grid[i][j]!=grid[k][j])
                {
                    ++c2;
                }
                else if(grid[i][j]==1)
                {
                    ++o2;
                }
            }
        }
        if(grid[0].size()%2==1&&grid.size()%2==1)
        {
            if(o1+o2%2==1)
            {
                if(c1+c2>0)
                {
                    ans+=c1+c2;
                }
                else
                {
                    ans+=2;
                }
            }
            else
            {
                ans+=c1+c2;
            }
        }
        else
        {
            if(o1%2==1)
            {
                if(c1>0)
                {
                    ans+=c1;
                }
                else
                {
                    ans+=2;
                }
            }
            else
            {
                ans+=c1;
            }
            if(o2%2==1)
            {
                if(c2>0)
                {
                    ans+=c2;
                }
                else
                {
                    ans+=2;
                }
            }
            else
            {
                ans+=c2;
            }
        }
        if(grid.size()%2==1&&grid[0].size()%2==1)
        {
            if(grid[grid.size()/2][grid[0].size()/2]==1)
            {
                ++ans;
            }
        }
        return ans;
    }
};
