func minFlips(grid [][]int) int {
	m, n := len(grid), len(grid[0])
	res := 0
	for i := 0; i < m/2; i++ {
		for j := 0; j < n/2; j++ {
			g := grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j]
			res += min(g, 4-g)
		}
	}
	g, cnt := 0, 0
	if m%2 != 0 && n%2 != 0 {
        for j := 0; j < n/2; j++ {
			if grid[m/2][j] != grid[m/2][n-1-j] {
				cnt++
			} else {
				g = (g + grid[m/2][j] + grid[m/2][n-1-j]) % 4
			}
		}
        for i := 0; i < m/2; i++ {
			if grid[i][n/2] != grid[m-1-i][n/2] {
				cnt++
			} else {
				g = (g + grid[i][n/2] + grid[m-1-i][n/2]) % 4
			}
		}
        if cnt > 0 {
            res += cnt
        } else {
            res += g
        }
        res += grid[m/2][n/2]
	} else if m%2 != 0 {
		for j := 0; j < n/2; j++ {
			if grid[m/2][j] != grid[m/2][n-1-j] {
				cnt++
			} else {
				g = (g + grid[m/2][j] + grid[m/2][n-1-j]) % 4
			}
		}
        if cnt > 0 {
            res += cnt
        } else {
            res += g
        }
	} else if n%2 != 0 {
        for i := 0; i < m/2; i++ {
			if grid[i][n/2] != grid[m-1-i][n/2] {
				cnt++
			} else {
				g = (g + grid[i][n/2] + grid[m-1-i][n/2]) % 4
			}
		}
        if cnt > 0 {
            res += cnt
        } else {
            res += g
        }
	}
	return res
}
