class Solution {
    func minFlips(_ grid: [[Int]]) -> Int {
        let rows = grid.count
        let columns = grid[0].count
        var flipCount = 0


        if rows % 2 == 1 && columns % 2 == 1 {
            if grid[rows / 2][columns / 2] == 1 {
                flipCount += 1
            }
        }

        //      if rows % 2 == 0 && columns % 2 == 0 {
        //     if grid[rows / 2][columns / 2] == 1 {
        //         flipCount += 2
        //     }
        // }

        var pairCounts = [0, 0, 0]  


        if rows % 2 == 1 {
            var left = 0
            var right = columns - 1
            while left < right {
                let sum = grid[rows / 2][left] + grid[rows / 2][right]
                pairCounts[sum] += 1
                left += 1
                right -= 1
            }
        }


        if columns % 2 == 1 {
            var top = 0
            var bottom = rows - 1
            while top < bottom {
                let sum = grid[top][columns / 2] + grid[bottom][columns / 2]
                pairCounts[sum] += 1
                top += 1
                bottom -= 1
            }
        }


        if pairCounts[2] % 2 == 1 {
            if pairCounts[1] > 0 {
                flipCount += pairCounts[1]
            } else {
                flipCount += 2
            }
        } else {
            flipCount += pairCounts[1]
        }


        var row = 0
        while row < rows / 2 {
            var column = 0
            while column < columns / 2 {
                let topLeft = grid[row][column]
                let topRight = grid[row][columns - 1 - column]
                let bottomLeft = grid[rows - 1 - row][column]
                let bottomRight = grid[rows - 1 - row][columns - 1 - column]

                let sum = topLeft + topRight + bottomLeft + bottomRight

                if sum != 0 && sum != 4 {
                    flipCount += min(sum, 4 - sum)
                }

                column += 1
            }
            row += 1
        }

        return flipCount
    }
}
