class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        vector<vector<int>> change(n,vector<int>(m,0));
        int ans=0;
        int r=0,c=0;
        for(int i=0,j=n-1;i<=j;i++,j--){
            for(int u=0,v=m-1;u<=v;u++,v--){
                if(i==j || u==v){
                    if(i==j && u!=v){
                        if(grid[i][u]!=grid[i][v]){
                            ans++;
                            r++;
                        }
                    }else if(u==v && i!=j){
                        if(grid[i][u]!=grid[j][u]){
                            ans++;
                            c++;
                        }
                    }
                    continue;
                }
                int one=0,zero=0;
                if(grid[i][u]==1)one++;
                else zero++;
                if(grid[j][u]==1)one++;
                else zero++;
                if(grid[i][v]==1)one++;
                else zero++;
                if(grid[j][v]==1)one++;
                else zero++;
                if(one==zero)ans+=2;
                else if(!(one==4 || zero==4))ans++;
            }
        }
        if(n%2==0 && m%2==0)
         return ans;

         if(n%2 && m%2==0){
            int idx=n/2;
            int one=0;
            for(int i=0,j=m-1;i<=j;i++,j--){
                if(grid[idx][i]==grid[idx][j] && grid[idx][i]==1)one++;
            }
            if((2*one)%4==0)return ans;
            else if(r>=1) return ans;
            else return ans+2;
         }else if(m%2 && n%2==0){
            int idx=m/2;
            int one=0;
            for(int i=0,j=n-1;i<=j;i++,j--){
                if(grid[i][idx]==grid[j][idx] && grid[i][idx]==1)one++;
            }
            if((2*one)%4==0)return ans;
            else if(c>=1) return ans;
            else return ans+2;
         }else{
            if(grid[n/2][m/2]==1)ans++;
            int idx1=n/2;
            int one=0;
            for(int i=0,j=m-1;i<j;i++,j--){
                if(grid[idx1][i]==grid[idx1][j] && grid[idx1][i]==1)one++;
            }
            int idx2=m/2;
             for(int i=0,j=n-1;i<j;i++,j--){
                if(grid[i][idx2]==grid[j][idx2] && grid[i][idx2]==1)one++;
            }
            if((2*one)%4==0)return ans;
            if(r+c>=1)return ans;
            return ans+2;
         }
         return 0;
    }
};
