class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size(), m = grid[0].size(); 
        int ans = 0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int cnt1 = 0, cnt0 = 0;
                if(grid[i][j]) cnt1++;
                else cnt0++;
                if(grid[i][m-j-1]) cnt1++;
                else cnt0++;
                if(grid[n-i-1][j]) cnt1++;
                else cnt0++;
                if(grid[n-i-1][m-j-1]) cnt1++;
                else cnt0++;
                if(cnt1 == 0 || cnt0 == 0) continue;
                if(cnt1 == 3 || cnt0 == 3) ans++;
                if(cnt1 == 2 || cnt0 == 2) ans+=2;
            }
        }
        if(n%2 && m%2 && grid[n/2][m/2] == 1) ans++;
        int zo = 0, oo = 0;
        if(m%2){
            for(int i=0; i<n/2; i++){
                if(grid[i][m/2] + grid[n-i-1][m/2] == 1) zo++;
                else if(grid[i][m/2] + grid[n-i-1][m/2] == 2) oo++;
            }
        }
        if(n%2){
            for(int i=0; i<m/2; i++){
                if(grid[n/2][i] + grid[n/2][m-i-1] == 1) zo++;
                else if(grid[n/2][i] + grid[n/2][m-i-1] == 2) oo++;
            }
        }

        if(oo%2 == 0){
            return ans + zo;
        }
        else{
            if(zo) return ans + zo;
            else{
                return ans + 2;
            }
        }


    }
};
