class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        ans = 0

        for i in range(len(grid) // 2):
            for j in range(len(grid[0]) // 2):
                row = grid[i]
                vals = row[j] + row[-j - 1]  + grid[-i - 1][j] + grid[-i - 1][-j - 1]
                ans += min(4 - vals, vals)

        ones = 0
        diffs = 0

        if len(grid) % 2 == 1:
            row = grid[len(grid) // 2]
            for j in range(len(grid[0]) // 2):
                vals = row[j] + row[-j - 1]
                if row[j] != row[-j - 1]:
                    diffs += 1
                ones += vals

        if len(grid[0]) % 2 == 1:
            for i in range(len(grid) // 2):
                vals = grid[i][len(grid[0]) // 2] + grid[-i - 1][len(grid[0]) // 2]
                if grid[i][len(grid[0]) // 2] != grid[-i - 1][len(grid[0]) // 2]:
                    diffs += 1
                ones += vals

        ans += diffs
        ones %= 4

        if diffs < ones and (ones - diffs <= 0 or ones + diffs < 4):
            ans += 2

        if len(grid) % 2 == 1 and len(grid[0]) % 2 == 1:
            ans += grid[len(grid) // 2][len(grid[0]) // 2]

        return ans

