class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
      int n = si(grid), m = si(grid[0]);
      int ans = 0;
      int t11 = 0, core = 0;
      int t00 = 0;
      int t01 = 0;
      int rem = 0;

      for (int i = 0; i < (n+1)/2; i++) {
        for (int j = 0; j < (m+1) /2;j++) {
          vector<int> cn(2);
          vector<pair<int, int>> a = {{i, j}, {n-1-i, j}, {i, m-1-j}, {n-1-i, m-1-j}};
          set<pair<int, int>> st(all(a));
          for (auto [x, y] : st) {
            for (int k = 0; k < 2; k++) {
              cn[k] += grid[x][y] != k;
            }
          }
          int cur = min(cn[0], cn[1]);
          ans += cur;
          if (si(st) == 1) core++, rem += cn[1] == 0;
          else if (si(st) == 2) {
            if (cn[0] == 0) t00++;
            else if (cn[1] == 0) t11++, rem += 2;
            else t01++;
          }
        }
      }
      rem %= 4;
      if (rem == 1) {
        ans++;
      } else if (rem == 2) {
        if (!t01) ans += 2;
      } else if (rem == 3) {
        ans++;
        if (!t01) ans += 2;
      }
      return ans;
    }
};
