class Solution {
    public int minFlips(int[][] grid) {
        int count = 0;

        int cx = (grid[0].length) / 2;
        int cy = (grid.length) / 2;

        for (int i = 0; i < cy; i++) {
            for (int j = 0; j < cx; j++) {
                int count1 = 0;
                count1 += grid[i][j];
                count1 += grid[grid.length - 1 - i][j];
                count1 += grid[grid.length - 1 - i][grid[0].length - 1 - j];
                count1 += grid[i][grid[0].length - 1 - j];

                count += Math.min(count1, 4 - count1);
            }
        }
        System.out.println(cx + " " + cy);

        if (grid.length % 2 == 0 && grid[0].length % 2 == 0) {
            return count;
        }

        int[] freqs = new int[3];
        if (grid[0].length % 2 != 0) {
            for (int i = 0; i < cy; i++) {
                int count1 = 0;
                count1 += grid[i][cx];
                count1 += grid[grid.length - 1 - i][cx];
                freqs[count1]++;
            }
        }

        if (grid.length % 2 != 0) {
            for (int j = 0; j < cx; j++) {
                int count1 = 0;
                count1 += grid[cy][j];
                count1 += grid[cy][grid[0].length - 1 - j];
                freqs[count1]++;
            }
        }


        // System.out.println(Arrays.toString(freqs));
        if (grid.length % 2 == 1 && grid[0].length % 2 == 1) {
            if (grid[cy][cx] == 1) {
                count++;
            }
        }

        // System.out.println(Arrays.toString(freqs));

        freqs[2] %= 2;



        if (freqs[2] == 1 && freqs[1] >= 1) {
            count++;
            freqs[1]--;
        } else if (freqs[2] == 1) {
            count += 2;
        }

        count += freqs[1];
        return count;
    }
}
