class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int res = 0;
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                //
                auto t1_x=i;
                auto t1_y=m-1-j;
                //
                auto t2_x=n-1-i;
                auto t2_y=j;
                //
                auto t3_x=t2_x;
                auto t3_y=t1_y;

                int cnt=0;
                cnt+=grid[i][j];
                cnt+=grid[t1_x][t1_y];
                cnt+=grid[t2_x][t2_y];
                cnt+=grid[t3_x][t3_y];
                if(cnt>=2) {
                    res+=4-cnt;
                }else{
                    res+=cnt;
                }

            }
        }
        int c1=0;
        int cx=0;
        if(n%2==1) {
            auto k=m-1;

            for(int i=0;i<=k;i++,k--) {
                if(grid[n/2][i]!=grid[n/2][k]) {
                    res++;
                    cx++;
                }else{
                    if(grid[n/2][i]) {
                        c1++;
                    }
                }
            }

        }
        int c21=0,c2x=0;
        if(m%2==1) {
            auto k=n-1;

            for(int i=0;i<=k;i++,k--) {
                if(grid[i][m/2]!=grid[k][m/2]) {
                    res++;
                    c2x++;
                }else{
                    if(grid[i][m/2]) {
                        c21++;
                    }
                }
            }
        }
        if(m%2==1&&n%2==1&&grid[n/2][m/2]) {
            res++;
        }
        //cout<<c1<<","<<c21<<","<<cx<<","<<c2x<<","<<endl;
        if((c1%2!=c21%2)&&cx+c2x==0) {
            res+=2;
        }

        return res;
    }
};
