class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int i = 0;
        int a = 0;

        while (i < m - 1 - i) {
            int j = 0;
            while (j < n - 1 - j) {
                int x = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][n - 1 - j];
                a += min(x, 4 - x);
                ++j;
            }
            ++i;
        }

        if (m % 2 > 0 && n % 2 > 0) {
            a += grid[m / 2][n / 2];
        }

        int x = 0;
        int c = 0;
        int c1 = 0;

        if (m % 2 > 0) {
            int i = m / 2;
            int j = 0;
            while (j < n - 1 - j) {
                ++c;
                if (grid[i][j] != grid[i][n - 1 - j]) {
                    ++x;
                } else if (grid[i][j] > 0) {
                    ++c1;
                }
                ++j;
            }
        }

        if (n % 2 > 0) {
            int i = 0;
            int j = n / 2;
            while (i < m - 1 - i) {
                ++c;
                if (grid[i][j] != grid[m - 1 - i][j]) {
                    ++x;
                } else if (grid[i][j] > 0) {
                    ++c1;
                }
                ++i;
            }
        }

        if (x == 0) {
            if (c1 % 2 > 0) {
                x += 2;
            }
        }

        return a + x;
    }
};
