class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0;
        for (int i=0; i<n/2; i++) {
            for (int j=0; j<m/2; j++) {
                int c1 = 0;
                if (g[i][j]) c1++;
                if (g[i][m-1-j]) c1++;
                if (g[n-1-i][j]) c1++;
                if (g[n-1-i][m-1-j]) c1++;
                ans += min(c1, 4-c1);
            }
        }
        int c11 = 0, c01 = 0;
        if (n%2) {
            int l = 0, r = m-1;
            while (l<r) {
                if (g[n/2][l] != g[n/2][r]) c01++;
                else if (g[n/2][l] == 1) c11++;
                l++;
                r--;
            }
        }
        if (m%2) {
            int l = 0, r = n-1;
            while (l<r) {
                if (g[l][m/2] != g[r][m/2]) c01++;
                else if (g[l][m/2] == 1) c11++;
                l++;
                r--;
            }
        }
        if (c01) {
            ans += c01;
        } else if (c11%2) {
            ans += 2;
        }
        if (n%2 && m%2 && g[n/2][m/2]) {
            ans++;
        }
        return ans;
    }
};
