class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        x, y = len(grid), len(grid[0])
        flip = 0
        for i in range(x//2):
            for j in range(y//2):
                tmp_sum = grid[i][j]+grid[x-1-i][j]+grid[i][y-1-j]+grid[x-1-i][y-1-j]
                flip += 2 - abs(tmp_sum-2)
        add_flip = 0
        add_sum = 0
        if x%2 ==1:
            add_flip += sum(grid[int((x-1)/2)][i]!=grid[int((x-1)/2)][y-1-i] for i in range(y//2))
            add_sum += sum(grid[(x-1)//2]) - (grid[x//2][y//2] if y%2==1 else 0)
        if y%2 ==1:
            add_flip += sum(grid[i][int((y-1)/2)]!=grid[x-1-i][int((y-1)/2)] for i in range(x//2))
            add_sum += sum(grid[i][(y-1)//2] for i in range(x)) - (grid[x//2][y//2] if x%2==1 else 0)
        if x%2==1 and y%2==1 and grid[x//2][y//2]==1:
            flip += 1
        if add_flip==0 and add_sum%4==2:
            add_flip = 2
        return flip+add_flip
