class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0,o=0,zo=0,oo=0,zz=0;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                set<pair<int,int>>s;
                s.insert({i,j});
                s.insert({n-1-i,m-1-j});
                s.insert({i,m-1-j});
                s.insert({n-1-i,j});
                int ones=0,zeros=0;
                for(auto &p:s){
                    if(grid[p.first][p.second]){
                        ones++;
                    }
                    else{
                        zeros++;
                    }
                }
                if(s.size()==4){
                    if(ones>=2){
                        o+=4;
                    }
                    ans+=min(ones,zeros);
                }
                else if(s.size()==2){
                    if(ones==2){
                        oo++;
                        o+=2;
                    }
                    else if(ones==1){
                        zo++;
                        o++;
                    }
                    else{
                        zz++;
                    }
                }
                else{
                    if(ones==1){
                        ans++;
                    }
                }
            }
        }
        // cout<<zo<<" z "<<o<<endl;
        if(!zo && (o%4)==0){
            return ans;
        }
        bool f=false;
        for(int i=0;i<=zo;i++){
            int newO=o-i+((zo-i));
            if(newO%4==0){
                f=true;
                break;
            }
        }
        if(f){
            ans+=zo;
        }
        else{
            ans+=zo+2;
        }
        return ans;
    }
};
