class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length, ans = 0;
        if ((m&1) == 1 && (n&1) == 1 && grid[n/2][m/2] == 1) ans++;
        int ones = 0, var = 0;
        for (int i = 0; i < n/2; i++){
            for (int j = 0; j < m/2; j++){
                int cnt = 0;
                if (grid[i][j] == 1) cnt++;
                if (grid[i][m-1-j] == 1) cnt++;
                if (grid[n-1-i][j] == 1) cnt++;
                if (grid[n-1-i][m-1-j] == 1) cnt++;
                int some = Math.min((cnt % 4), (4 - (cnt % 4)));
                ans += some;
            }
        }

        if ((n&1) == 1){
            for (int i = 0; i < m/2; i++){
                if (grid[n/2][i] != grid[n/2][m-1-i]){
                    ans++;
                    var++;
                }
                else {
                    if (grid[n/2][i] == 1) ones += 2;
                }
            }
        }

        if ((m&1) == 1){
            for (int i = 0; i < n/2; i++){
                if (grid[i][m/2] != grid[n-1-i][m/2]){
                    ans++;
                    var++;
                }
                else {
                    System.out.println(i + " " + m/2 + " " + grid[i][m/2]);
                    if (grid[i][m/2] == 1) ones += 2;
                }
            }
        }

        if (var > 0) return ans;
        if (ones % 4 == 0) return ans;
        return ans+2;
    }

    private int getVal(int n){
        int ans = (Math.min(n%4, 4-(n%4)));
        return ans;
    }

    private int getCnt(int[][] arr){
        int cnt = 0;
        for (int i = 0; i < arr.length; i++){
            for (int j = 0; j < arr[0].length; j++){
                if (arr[i][j] == 1) cnt++;
            }
        }
        return cnt;
    }

    private int getRow(int[][] arr){
        int cnt = 0;
        for (int i = 0; i < arr.length; i++){
            for (int j = 0; j < arr[0].length/2; j++){
                if (arr[i][j] != arr[i][arr[0].length-1-j]) cnt++;
            }
        }
        return cnt;
    }

    private int getCol(int[][] arr){
        int cnt = 0;
        for (int i = 0; i < arr[0].length; i++){
            for (int j = 0; j < arr.length/2; j++){
                if (arr[j][i] != arr[arr.length-1-j][i]) cnt++;
            }
        }
        return cnt;
    }
}
