class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int ans1=0;
        int sum=0;
        int bj=0;
        int m=grid.size();
        int n=grid[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int sum=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                if(sum==0||sum==4){
                    ans=ans+0;
                }
                else if(sum==1||sum==3){
                    ans++;
                }
                else{
                    ans+=2;
                }
            }
        }
        if(m%2!=0||n%2!=0){
            if(m%2==0){
                for(int i=0;i<m/2;i++){
                    if(grid[i][n/2]!=grid[m-1-i][n/2]){
                        bj=1;
                        ans1++;
                    }
                }
                for(int i=0;i<m;i++){
                    sum+=grid[i][n/2];
                }
            }
            else if(n%2==0){
                for(int i=0;i<n/2;i++){
                    if(grid[m/2][i]!=grid[m/2][n-1-i]){
                        bj=1;
                        ans1++;
                    }
                }
                for(int i=0;i<n;i++){
                    sum+=grid[m/2][i];
                }
            }
            else{
                for(int i=0;i<m/2;i++){
                    if(grid[i][n/2]!=grid[m-1-i][n/2]){
                        bj=1;
                        ans1++;
                    }
                }
                for(int i=0;i<n/2;i++){
                    if(grid[m/2][i]!=grid[m/2][n-1-i]){
                        bj=1;
                        ans1++;
                    }
                }
                for(int i=0;i<m;i++){
                    sum+=grid[i][n/2];
                }
                for(int i=0;i<n;i++){
                    sum+=grid[m/2][i];
                }
                sum-=2*grid[m/2][n/2];
                ans1+=grid[m/2][n/2];
            }
            if(bj==0&&sum%4==2){
                ans1+=2;
            }

        }
        return ans+ans1;
    }
};
