class Solution {
    class Freq{
        public int oneFreq;
        public int zeroFreq;
        public Freq(int a, int b){
            oneFreq = a;
            zeroFreq = b;
        }
    }
    int m;
    int n;
    int[][] grid;
    boolean[][] visited;
    public int minFlips(int[][] grid) {

        m = grid.length;
        n = grid[0].length;
        this.grid = grid;
        visited = new boolean[m][n];
        int pairs_of_1 = 0;
        int pairs_of_0_1 = 0;
        int flips = 0;

        for(int i=0 ; i<m ; i++){
            for(int j=0; j<n ; j++){
                if(visited[i][j]){
                    continue;
                }

                Freq freq = countFreq(i,j);
                int oneFreq = freq.oneFreq;
                int zeroFreq = freq.zeroFreq;
                if(oneFreq == 1 && zeroFreq == 0 ){
                    flips+=1;
                }else if(oneFreq == 2 && zeroFreq == 0){
                    pairs_of_1+=1;
                }else if(oneFreq == 1 && zeroFreq == 1){
                    pairs_of_0_1 += 1;
                    flips += 1;
                }else{
                    flips += Math.min(oneFreq, zeroFreq);
                }
            }
        }

        if(pairs_of_1 % 2 != 0){
            flips+= (pairs_of_0_1 == 0 ? 2 : 0);
        }




        return flips;

    }

    private Freq countFreq(int i, int j){
        int oneFreq = 0;
        int zeroFreq = 0;
        int[][] pairs = new int[][]{{i,j}, {m-1-i,j},{i,n-1-j}, {m-1-i,n-1-j}};
        for(int[] pair : pairs){
            int a = pair[0], b = pair[1];
            if(!visited[a][b]){
                oneFreq += (grid[a][b] == 1 ? 1 : 0);
                zeroFreq += (grid[a][b] == 0 ? 1 : 0);
            }
            visited[a][b] = true;
        }

        // System.out.println(oneFreq);
        // System.out.println(zeroFreq);

        return new Freq(oneFreq, zeroFreq);

    }
}
