class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        // cout<<m<<" "<<n;
        for (int i = 0;i<n/2;i++){
            for (int j = 0;j<m/2;j++){
                int count = 0;
                count += grid[i][j];
                count += grid[i][m - j -1];
                count += grid[n - i -1][j];
                count += grid[n - i -1][m - j -1];
                ans += min(count, 4 - count);
            }
        }
        if (n%2!= 1 && m%2!=1)  return ans;
        int countn = 0;
        int ones = 0;
        if (n%2){
            int i = ((n == 1) ? 0 : n/2);
            for (int j = 0;j<m/2;j++){
                countn += (grid[i][j] != grid[i][m - j - 1]);
                if (grid[i][j] == grid[i][m- j - 1] && grid[i][j] == 1){
                    ones+=2;
                }
            }
            if (m%2)    ans+=grid[i][m/2];
        }
        int countm = 0;
        if (m%2){
            int j = ((m == 1) ? 0 : m/2);
            // cout<<j<<" ";
            for (int i = 0;i<n/2;i++){
                // cout<<i<<" "<<grid[i][j]<<" ";
                countm += (grid[i][j] != grid[n - i - 1][j]);
                if (grid[i][j] == grid[n - i - 1][j] && grid[i][j] == 1){
                    ones+=2;
                }
            }
        }
        int count = countn + countm;
        ans+=count;
        if (ones%4 == 2 && count>=1){
            ones+=2;
        }
        int mod = ones%4;
        ans+=min(mod, 4 - mod);
        return ans;
    }
};
