class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int count=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1)
                count++;
            }
        }
       vector<vector<int> >vis(n,vector<int>(m,0));
       vector<pair<int,int> >vp;
         for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==1)
                continue;
                else{
                    int one=0;
                    int c=0;
                    if(vis[i][j]==0){
                        if(grid[i][j]==1)
                        one++;
                        c++;
                        vis[i][j]=1;
                    }
                    if(vis[n-i-1][m-j-1]==0){
                        if(grid[n-i-1][m-j-1]==1)
                        one++;
                        c++;
                        vis[n-i-1][m-j-1]=1;;
                    }
                    if(vis[i][m-j-1]==0){
                         if(grid[i][m-j-1]==1)
                        one++;
                        c++;
                        vis[i][m-j-1]=1;
                    }
                    if(vis[n-i-1][j]==0){
                         if(grid[n-i-1][j]==1)
                        one++;
                        c++;
                        vis[n-i-1][j]=1;
                    }
                vp.push_back(make_pair(one,c));
                }
            }
        }
        int oper=0;
        int q=vp.size();
        vector<int>vk(q,0);
        int c=0;
        for(int i=0;i<q;i++){
            int u=vp[i].first;
            int v=vp[i].second;
            cout<<u<<" "<<v<<endl;
            if(u<v-u)
            {
                count-=u;
                oper+=u;
                vk[i]=-1;
            }
            else{
                count+=v-u;
                oper+=v-u;
                vk[i]=1;
            }
            if(v==1&&u==1)
            c=1;
        }
        cout<<oper<<" "<<count<<endl;
        if(count%4==0)
        return oper;
        if(count%4==1&&c==1)
        return oper+1;
        if(count%4==3&&c==0)
        return oper+1;
        if(count%4==3)
        oper++;
        int ans=4;
        for(int i=0;i<q;i++){
            int u=vp[i].first;
            int v=vp[i].second;
           if(v==2){
            if(u==1)
            ans=min(ans,0);
            else
            ans=min(ans,2);
           }
        }
        return oper+ans;



    }
};
