class Solution {
public:
    int n,m;
    int minFlips(vector<vector<int>>& grid) {
        n = grid.size();
        m = grid[0].size();
        int res = 0;

        vector<int> rem;

        for(int i=0;i<ceil(n/2.0);i++) {
            for(int j=0;j<ceil(m/2.0);j++) {

                int nxt = m-j-1;

                // 1
                int cnt = 0;
                int totalOne = 1;
                if(nxt!=j) totalOne++;

                if(grid[i][j]!=1) cnt++;
                if(nxt!=j && grid[i][nxt]!=1){
                    cnt++;
                }
                int nxtI = n-i-1;
                if(nxtI!=i) totalOne++;
                if(nxt!=j && nxtI!=i) totalOne++;

                if(nxt == j && nxtI == i) {
                    if(grid[i][j]) res++;
                    continue;
                }

                if(nxtI!=i && grid[nxtI][j]!=1) cnt++;

                if(nxt!=j && nxtI!=i && grid[nxtI][nxt]!=1) cnt++;

                // cout<<"i: "<<i<<" j: "<<j<<" nxt:"<<nxt<<" nxtI: "<<nxtI<<endl;
                int mn = cnt;
                int oneCost = cnt;
                if(totalOne == 2 ){
                    mn = INT_MAX;
                }

                // cout<<"One: "<<totalOne<<endl;
                // // 0
                cnt = 0;

                if(grid[i][j]!=0) cnt++;
                if(nxt!=j && grid[i][nxt]!=0) cnt++;

                if(nxtI!=i && grid[nxtI][j]!=0) cnt++;

                if(nxt!=j && nxtI!=i && grid[nxtI][nxt]!=0) cnt++;
                mn = min(cnt, mn);
                if(totalOne == 2){
                    rem.push_back(oneCost - mn);
                }
                res += mn;
            }
        }
        sort(begin(rem),end(rem));
        for(int i=0;i<rem.size();i+=2) {
            if(i==rem.size()-1) continue;
            if(rem[i] + rem[i+1] < 0) {
                res += rem[i] + rem[i+1];
            }
        }
        return res;
    }
};
