class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int flips=0;
        for(int i=0;i<n/2;i++){
          for(int j=0;j<m/2;j++){
            int row1=i;
            int row2=n-i-1;
            int col1=j;
            int col2=m-j-1;
            int cnt_ones=(grid[row1][col1]==1);
            cnt_ones+=(grid[row1][col2]==1);
            cnt_ones+=(grid[row2][col1]==1);
            cnt_ones+=(grid[row2][col2]==1);
            flips+=min(cnt_ones,4-cnt_ones);
          }
        }

        int cnt_ones=0;
        int cnt_flips=0;
        if(n%2){

          int j=0;
          int k=m-1;
          while(j<k){
            if(grid[n/2][j]!=grid[n/2][k]){
              cnt_flips++;
              cnt_ones++;
            }
            else{
              if(grid[n/2][j]){
                cnt_ones+=2;
              }
            }
            j++;
            k--;
          }
        }

        if(m%2){

          int j=0;
          int k=n-1;
          while(j<k){
            if(grid[j][m/2]!=grid[k][m/2]){
              cnt_flips++;
              cnt_ones++;
            }
            else{
              if(grid[j][m/2]){
                cnt_ones+=2;
              }
            }
            j++;
            k--;
          }
        }

        // flips+=cnt_flips;
        int cnt1=(cnt_ones+cnt_flips)%4;
        int cnt2=(cnt_ones-cnt_flips+4)%4;
        flips+=max(cnt_flips,min(cnt1,cnt2));

        if(n%2 && m%2 && grid[n/2][m/2]){
          flips++;
        }

        return flips;
    }
};
