class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int i=0; int j=0;
        int ans=0;
        int c=0;
        // while(i<n/2 && j<m/2){
        //     c=grid[i][j] + grid[n-1-i][j] + grid[n-1-i][m-1-j] + grid[i][m-1-j];
        //     ans+=min(c, 4-c);
        //     i++;
        //     j++;
        // }
        for(i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                c=grid[i][j] + grid[n-1-i][j] + grid[n-1-i][m-1-j] + grid[i][m-1-j];
                ans+=min(c, 4-c);
            }
        }
        // i++; j++;
        // cout<<i<<' '<<j<<'\n';
        i=n/2 ;
        j=m/2 ;

        while(i<n/2 && j!=m-1-j){
            c=grid[i][j] + grid[n-1-i][j] + grid[n-1-i][m-1-j] + grid[i][m-1-j];
            ans+=min(c, 4-c);
            i++;
        }

        while(j<m/2 && i!=n-1-i){
            c=grid[i][j] + grid[n-1-i][j] + grid[n-1-i][m-1-j] + grid[i][m-1-j];
            ans+=min(c, 4-c);
            j++;
        }

        int cp=0;
        int ansP=0;

        if(j==(m-1)/2){
            for(int k=0; k<n/2; k++){
                if(grid[k][j]!=grid[n-1-k][j]){
                    ansP+=1;
                }
                else if(grid[k][j]==1){
                    cp+=1;
                }
            }
        }

        if(i==(n-1)/2){
            for(int k=0; k<m/2; k++){
                if(grid[i][k]!=grid[i][m-1-k]){
                    ansP++;
                }
                else if(grid[i][k]==1){
                    cp++;
                }
            }
        }


        if(n%2==1 && m%2==1){
            ans+=grid[(n-1)/2][(m-1)/2];
        }
        cout<<ans<<' '<<ansP<<' '<<cp<<' '<<i<<' '<<n<<' '<<j<<' '<<m;
        if(ansP==0 && cp%2==1){
            ans+=2;
        }
        ans+=ansP;
        return ans;


    }
};
