class Solution:
    def minFlips(self, grid):
        m, n, res = len(grid), len(grid[0]), 0

        for i in range(m):
            for j in range(n):
                si, sj = m - i - 1, n - j - 1
                if i < si and j < sj:
                    c1 = grid[i][j] + grid[si][sj] + grid[si][j] + grid[i][sj]
                    res += min(c1, 4 - c1)

        if m % 2 == 1 and n % 2 == 1 and grid[m // 2][n // 2] == 1: res += 1

        t00, t01, t11 = 0, 0, 0

        for i in range(m):
            for j in range(n):
                si, sj = m - i - 1, n - j - 1
                if i == si and j < sj:
                    if grid[i][j] and grid[i][sj]:
                        t11 += 1
                    elif not grid[i][j] and not grid[i][sj]:
                        t00 += 1
                    else:
                        t01 += 1

                if i < si and j == sj:
                    if grid[i][j] and grid[si][j]:
                        t11 += 1
                    elif not grid[i][j] and not grid[si][j]:
                        t00 += 1
                    else:
                        t01 += 1

        if t01 == 0 and t11 % 2 == 1: res += 2

        if t01 > 0: res += t01

        return res
