class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int cnt1 = 0, cnt0 = 0;
                a[i][j] == 0 ? cnt0++ : cnt1++;
                a[i][m - j - 1] == 0 ? cnt0++ : cnt1++;
                a[n - i - 1][j] == 0 ? cnt0++ : cnt1++;
                a[n - i - 1][m - j - 1] == 0 ? cnt0++ : cnt1++;
                ans += min(cnt1, cnt0);
            }
        }
        if((n & 1) && (m & 1)){
            int cnt1 = 0, not_equal = 0;
            for(int j = 0; j < m / 2; j++){
                if((a[n / 2][j] == 1) && (a[n / 2][m - j - 1] == 1)) cnt1++;
                if(a[n / 2][j] != a[n / 2][m - j - 1]) not_equal++;
            }
            for(int i = 0; i < n / 2; i++){
                if((a[i][m / 2] == 1) && (a[n - i - 1][m / 2] == 1)) cnt1++;
                if(a[i][m / 2] != a[n - i - 1][m / 2]) not_equal++;
            }
            ans += not_equal;
            if((cnt1 & 1) && (not_equal == 0)) ans += 2;
            if(a[n / 2][m / 2] == 1) ans++;
        }
        else if(n & 1){
            int cnt1 = 0, not_equal = 0;
            for(int j = 0; j < m / 2; j++){
                if((a[n / 2][j] == 1) && (a[n / 2][m - j - 1] == 1)) cnt1++;
                if(a[n / 2][j] != a[n / 2][m - j - 1]) not_equal++;
            }
            ans += not_equal;
            if((cnt1 & 1) && (not_equal == 0)) ans += 2;
        }
        else if(m & 1){
            int cnt1 = 0, not_equal = 0;
            for(int i = 0; i < n / 2; i++){
                if((a[i][m / 2] == 1) && (a[n - i - 1][m / 2] == 1)) cnt1++;
                if(a[i][m / 2] != a[n - i - 1][m / 2]) not_equal++;
            }
            ans += not_equal;
            if((cnt1 & 1) && (not_equal == 0)) ans += 2;
        }
        return ans;
    }
};
