class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size(),ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int tmp=a[i][j];
                tmp+=a[n-1-i][j];
                tmp+=a[i][m-1-j];
                tmp+=a[n-i-1][m-1-j];

                //if(tmp==0 || tmp==4) continue;
                //cout<<i<<" "<<j<<" "<<tmp<<endl;
                ans+=min(tmp,4-tmp);

            }
        }
        int x=0,y=0;
        if(n%2 && m%2 && a[n/2][m/2]) ans++;

        if(n%2){
            for(int j=0;j<m/2;j++){
                if(a[n/2][j]!=a[n/2][m-1-j]){
                    x+=2; 
                    ans++;
                }
                else if(a[n/2][j]) y+=2;
            }
        }

        if(m%2){
            for(int i=0;i<n/2;i++){
                if(a[i][m/2]!=a[n-1-i][m/2]){
                    x+=2;
                    ans++;
                }
                else if(a[i][m/2]) y+=2;
            }
        }

        if(y%4==0 || x) return ans;

        return ans+2;



    }
};
