class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        int vis[n][m];
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            vis[i][j] = 0;
        }
        int z00=0,z01=0,z11=0,l=-1;

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(vis[i][j] == 0)
                {
                    //i,j
                    //i,m-j-1
                    //n-i-1,j
                    //n-i-1,m-j-1
                    int z=0,o=0;
                    int f=0;
                    if(vis[i][j] == 0)
                    {
                        if(grid[i][j] == 0)
                        z++;
                        else
                        o++;
                        vis[i][j] =1;
                        f++;
                    }
                    if(vis[i][m-j-1] == 0)
                    {
                        if(grid[i][m-j-1] == 0)
                        z++;
                        else
                        o++;
                        vis[i][m-j-1] =1;
                        f++;
                    }
                    if(vis[n-i-1][j] == 0)
                    {
                        if(grid[n-i-1][j] == 0)
                        z++;
                        else
                        o++;
                        vis[n-i-1][j] =1;
                        f++;
                    }
                    if(vis[n-i-1][m-j-1] == 0)
                    {
                        if(grid[n-i-1][m-j-1] == 0)
                        z++;
                        else
                        o++;
                        vis[n-i-1][m-j-1] =1;
                        f++;
                    }

                    if(f == 4)
                    {
                      //  cout<<"g"<<z<<o;
                        ans += min(z,o);
                    }
                    else if(f == 2)
                    {
                        if(z==2)
                        z00++;
                        else if(z==1)
                        z01++;
                        else
                        z11++;
                    }
                    else if(f==1)
                    {
                        l = grid[i][j];
                    }


                }
            }
        }

        if(l == -1)
        {
            int maxi = z00 + z01 + z11;
            int mini = 1e9 + 7;
            // 11 comes maxi times
            for(int i=0;i<=maxi;i++)
            {
               // 11 i times
               // 00 maxi - i -1 times
               int cnt = 0;
               if((2*i)%4 == 0)
               {
                  int rest = z11 - i;
                  if(rest == 0)
                  {
                      cnt += z01;
                  }
                  else if(rest > 0)
                  {
                    cnt += 2*rest + z01;
                  }
                  else if(rest < 0)
                  {
                    if(z01 >= abs(rest))
                    cnt += z01;
                    else
                    {
                        int pq = abs(rest) - z01;
                        cnt += z01;
                        cnt += 2*pq;
                    }
                  }
                  mini = min(mini, cnt);
               }
            }

            return mini + ans;
        }
        else
        {
            // Case1 if middle is 0
            int c1 = 0, c2=0;
            int maxi = z00 + z01 + z11;
            int mini = 1e9 + 7;
            // 11 comes maxi times
            for(int i=0;i<=maxi;i++)
            {
               // 11 i times
               // 00 maxi - i -1 times
               int cnt = 0;
               if((2*i)%4 == 0)
               {
                  int rest = z11 - i;
                  if(rest == 0)
                  {
                      cnt += z01;
                  }
                  else if(rest > 0)
                  {
                    cnt += 2*rest + z01;
                  }
                  else if(rest < 0)
                  {
                    if(z01 >= abs(rest))
                    cnt += z01;
                    else
                    {
                        int pq = abs(rest) - z01;
                        cnt += z01;
                        cnt += 2*pq;
                    }
                  }
                  mini = min(mini, cnt);
               }
            }
            c1 = mini + ans + l;

            return c1;
        }



    }
};
