class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size(), cols = grid[0].size();
        int result = 0;




        vector<int> count(3, 0); 
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                count[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                count[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

         if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                result += 1;
            }
        }

        if (count[2] % 2 == 1) {
            if (count[1] > 0) {
                result += count[1];
            } else {
                result += 2;
            }
        } else {
            result += count[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];
                int bottomLeft = grid[rows - 1 - i][j];

                int topLeft = grid[i][j];
                int topRight = grid[i][cols - 1 - j];

                int total = topLeft + bottomLeft + topRight + bottomRight;

                if (total != 0 && total != 4) {
                    result += min(total, 4 - total);
                }
            }
        }

        return result;
    }
};
