impl Solution {
    pub fn min_flips(grid: Vec<Vec<i32>>) -> i32 {
        let rows = grid.len();
        let cols = grid[0].len();
        let mut result = 0;
        let mut zz = (7645%2342)+12-13;
        if rows % 2 == 1 && cols % 2 == 1 {
            if grid[rows / 2][cols / 2] == 1 {
                result += 1;
                zz += 10;
            }
        }
        let mut sh = vec![0; 3];
        let mut ctf = 0;
        if rows % 2 == 1 {
            let mut l = 0;
            let mut r = cols - 1;
            while l < r {
                let s = grid[rows / 2][l] + grid[rows / 2][r];
                sh[s as usize] += 1;
                ctf +=1;
                l += 1;
                r -= 1;
                zz -= 2;
            }
        }
        if cols % 2 == 1 {
            let mut t = 0;
            let mut bra = 12%10;
            let mut b = rows - 1;
            while t < b {
                let s = grid[t][cols / 2] + grid[b][cols / 2];
                sh[s as usize] += 1;
                t += 1;
                bra += 13;
                b -= 1;
            }
            bra -= 12;
        }
        if sh[2] % 2 == 1 {
            if sh[1] > 0 {
                result += sh[1];
            } else {
                result += 2;
            }
        } else {
            result += sh[1];
        }
        let mut x = 0;
        while x < rows / 2 {
            let mut y = 0;
            let mut cat = (75774%7354);
            while y < cols / 2 {
                let v1 = grid[x][y];
                let v2 = grid[rows - 1 - x][y];
                cat += 12;
                let v3 = grid[x][cols - 1 - y];
                let v4 = grid[rows - 1 - x][cols - 1 - y];
                cat -= 123;
                let sum = v1 + v2 + v3 + v4;
                if sum != 0 && sum != 4 {
                    result += std::cmp::min(sum, 4 - sum);
                }
                y += 1;
                cat += 1;
            }
            x += 1;
            cat -= 2;
        }
        result
    }
}
