class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        res = 0

        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                res += 1

        cnt = [0] * 3

        if n % 2 == 1:
            x, y = 0, m - 1
            while x < y:
                sm = grid[n // 2][x] + grid[n // 2][y]
                cnt[sm] += 1
                x += 1
                y -= 1

        if m % 2 == 1:
            x, y = 0, n - 1
            while x < y:
                sm = grid[x][m // 2] + grid[y][m // 2]
                cnt[sm] += 1
                x += 1
                y -= 1

        if cnt[2] % 2 == 1:
            if cnt[1] > 0:
                res += cnt[1]
            else:
                res += 2
        else:
            res += cnt[1]

        i = 0
        while i < n // 2:
            j = 0
            while j < m // 2:
                a = grid[i][j]
                b = grid[n - 1 - i][j]
                c = grid[i][m - 1 - j]
                d = grid[n - 1 - i][m - 1 - j]

                sm = a + b + c + d

                if sm != 0 and sm != 4:
                    res += min(sm, 4 - sm)

                j += 1
            i += 1

        return res
