class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:        
        numRows, numCols = len(grid), len(grid[0])
        totalFlips = 0

        if numRows % 2 == 1 and numCols % 2 == 1:
            if grid[numRows // 2][numCols // 2] == 1:
                totalFlips += 1

        shifts = [0] * 3
        if numRows % 2 == 1:
            left, right = 0, numCols - 1
            while left < right:
                sumFlips = grid[numRows // 2][left] + grid[numRows // 2][right]
                shifts[sumFlips] += 1
                left += 1
                right -= 1

        if numCols % 2 == 1:
            top, bottom = 0, numRows - 1
            while top < bottom:
                sumFlips = grid[top][numCols // 2] + grid[bottom][numCols // 2]
                shifts[sumFlips] += 1
                top += 1
                bottom -= 1

        if shifts[2] % 2 == 1:
            if shifts[1] > 0:
                totalFlips += shifts[1]
            else:
                totalFlips += 2
        else:
            totalFlips += shifts[1]

        for i in range(numRows // 2):
            for j in range(numCols // 2):
                a = grid[i][j]
                b = grid[numRows - 1 - i][j]
                c = grid[i][numCols - 1 - j]
                d = grid[numRows - 1 - i][numCols - 1 - j]

                sumFlips = a + b + c + d

                if sumFlips != 0 and sumFlips != 4:
                    totalFlips += min(sumFlips, 4 - sumFlips)

        return totalFlips
