class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0

        # 考察的四人组
        for i in range(m//2):
            for j in range(n // 2):
                x00 = grid[i][j]
                x01 = grid[i][-j-1]
                x10 = grid[-i-1][j]
                x11 = grid[-i-1][-j-1]
                # 要么全0，要么全1，不管怎样，不会改变倍数关系，就按照最小的来
                s = x00 + x01 + x10 + x11
                ans += min(s,4-s)

        # 考察中间一行或者中间一列

        # 中间一行，统计不同的个数
        cnt = 0 # 记录可以变0/变1的次数
        cnt1 = 0
        if m&1:
            for j in range(n//2):
                if grid[m//2][j]==1 and grid[m//2][-j-1]==1:
                    cnt1 += 2
                cnt +=  grid[m//2][j] != grid[m//2][-j-1]
        # 中间一列，统计不同的个数
        if n&1:
            for i in range(m//2):
                if grid[i][n//2]==1 and grid[-i-1][n//2]==1:
                    cnt1 += 2
                cnt += grid[i][n//2] != grid[-i-1][n//2]
        if m&1 and n&1:
            ans += grid[m//2][n//2]
        cnt1 %= 4
        if cnt1 == 0:
            return ans + cnt
        elif cnt1 == 2:
            if cnt>0:
                return cnt + ans
            else:
                return ans + 2
